		if(places > 0){
			for(int i=0; i<places; i++){
				factor *= 10;
			}
			value *= factor;
		}else{
			for(int i=0; i>places; i--){
				factor *= 10;
			}
			value /= factor;
		}
		value = Math.rint( value );
		if(places > 0){
			value /= factor;
		}else{
			value *= factor;
		}
		return value;
    }

